import createDataContext from './createDataContext';
import trackerAPI from '../api/tracker';

const authReducer = (state, action) => {

    switch (action.type) {
        default:
            return state;
    }
};

const signup = dispatch => {

    return async ({ email, password }) => {

        // make api request for signup
        // if signup success, modify state that we authenticated
        // if fails then return error message

        try {
            const response = await trackerAPI.post('/signup', { email, password });

            console.log(response.data);

        } catch (err) {

            console.log(err.response.data);
        }

    };
}


const signIn = dispatch => {

    return async ({ email, password }) => {

        // make api request for signin if signin success, 
        // modify state that we authenticated, if fails then return error message

        try {
            const response = await trackerAPI.post('/signin', { email, password });
            console.log(response.data);

        } catch (err) {
            console.log(err.response.data);
        }
    };
}


const signOut = (dispatch) => {
    return ({ email, password }) => {

    };
}


export const { Provider, Context } = createDataContext(
    authReducer,
    { signIn, signUp, signOut },
    { isSignedIn: false }
);
